import React, { Component } from 'react';

import MiComponente from './MiComponente';
import Peliculas from './Peliculas';

class SeccionPruebas extends Component {

    contador = 0;
    
    constructor(props){
        super(props);

        this.state = {
            contador: 0 
        };
    }


    //var HolaMundo= () =>{}
    HolaMundo(nombre, edad) {
        var presentacion = (
            <div>
                <h2>Hola soy, {nombre}</h2>
                <h3>Tengo {edad} años</h3>
            </div>
        );
        return presentacion;
    }

    sumar(){
        
        //this.contador = this.contador+1
        this.setState({
            contador: (this.state.contador +1) 
        });
    }

    restar(){
        this.contador = this.contador-1

    }



    render() {
        var nombre = "Matias Cisternas";

        return (
            <section id="content">
                <h2 class="subheader">Últimos Articulos</h2>
                <p>
                    Bienvenido al Restaurant SIGLO XXI
                </p>
                <h2 class="subheader">Funciones y JSX Basico</h2>

                {this.HolaMundo(nombre, 12)}

                <h2 class="subheader">Componentes</h2>

                <section className='componentes'>

                    <MiComponente />
                    <Peliculas />

                </section>
                <h2 class="subheader">Estado</h2>
                <p>
                Contado: {this.contador}
                </p>

                <p>
                <input type="button" value="Sumar" onClick={this.sumar.bind(this)}/>
                <input type="button" value="Restar"onClick={this.restar.bind(this)} />
               
                </p>
            </section>
        );
    }
}

export default SeccionPruebas;
