import React, {Component} from "react";
import {Apiproduct} from '../services/apirest';
import axios from 'axios';

class EditarProducto extends Component{

    state = {
        form:{
                "id": "",
                "name": "",
                "stock": "",
                                    
        },
        error:false,
        errorMsg:""
    }


    manejadorChange = async e => {
        await this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        })
     
  }
    
    manejadorSubmit = e => {
    e.preventDefault();
  }
  





  componentDidMount(){


    let idProduct = this.props.match.params.id;  
    let url = Apiproduct + idProduct; 
    axios.get(url)
    .then( response => {
       this.setState({
            form:{
                id: response.data.id,
                name:  response.data.name,
                stock:  response.data.stock    

            }
            
       })
      
    });
     
}


    render(){

    
        return(
               
         <React.Fragment>

         <br/>
         
         <div className="container">
                    <h3>Editar Producto </h3>
         </div>
         <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
       


         <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">id</label>
                    <div className="col-md-8">
                        <input className="form-control" name="id" placeholder="id producto" type="text" 
                        value={this.state.form.id}
                        onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                <br/>



                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Nombre</label>
                    <div className="col-md-8">
                        <input className="form-control" name="name" placeholder="Nombre producto" type="text" 
                        value={this.state.form.name}
                        onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                <br/>


                
         <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">stock</label>
                    <div className="col-md-8">
                        <input className="form-control" name="stock" placeholder="stock producto" type="text" 
                        value={this.state.form.stock}
                        onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                <br/>



          <br/>

              <button type="submit" className="btn btn-primary" style={{ marginRight: "10px" }}>Editar</button>
              <button type="submit" className="btn btn-danger" style={{ marginRight: "10px" }} onClick={()=>this.delete()} >Eliminar</button>
              <a className="btn btn-dark" href="/productos" >Salir</a>
              

          
          </form>
        </React.Fragment>
        
               
        );
    }

}


export default EditarProducto;
