import React, {Component} from "react";
import {ApiMenus,ApiCategory} from '../services/apirest';
import axios from 'axios';
import { createMenu } from "../converters/menuConverter";
import swal from 'sweetalert';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faTrash} from '@fortawesome/free-solid-svg-icons';
import {faEdit} from '@fortawesome/free-solid-svg-icons';
import { faArrowAltCircleLeft} from '@fortawesome/free-solid-svg-icons'
import Main2 from  './Main2'




class VerReceta extends Component{

  state={
    recipe:[]
} ;


    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };
    

 
      componentDidMount(){
        

    let idMenu = this.props.match.params.id;  
    let url = ApiMenus + idMenu; 
        axios.get(url)
        .then(response =>{
            this.setState({
                recipe : response.data.recipe[0]
            })
            
        })
}




    render(){

        const form = this.state.form;

        return(
               
          <React.Fragment>

          <Main2></Main2>
                
  <br></br>
                  <div className="container">
                      
  
                  <table id="customers" className="table table-striped">
                      <thead>
                          <tr>
                          <th scope="col">Nombre del usuario</th>
                          <th scope="col">Correo electr√≥nico</th>
          
                          </tr>
                      </thead>
                      <tbody> 
                          {this.state.recipe[0].map((value, index) => {
                             return (
                              <tr key={index}   >
                              <td>{value.recipe.product.name}</td>
                             
                              <td>
                                  <button >
                                  Editar
                                  </button>
                                  </td>
                              </tr>
                              )
                          })}
                           
                   
                      </tbody>
                      </table>
                      <div>
                          <a 
                          className="btn btn-success" 
                           href="/IngresarUsuario"
                           role="button"
                            style={{marginRight:"10px", float:"right"}}>
                              Ingresar usuario
                              </a>   
                       </div>
                  </div>
  
                      
              </React.Fragment>
        
               
        );
    }

}


export default VerReceta;
