import React, {Component} from "react";
import { ApiMesas } from "../services/apirest";
import axios from 'axios';
import HeaderRecetas from "../template/HeaderRecetas";
import Main2 from  './Main2';
//ESTA PANTALLA ES ADMIN

 
class VerReceta extends Component{

    state={
        recipe:[]
    } ;




    
    componentDidMount(){
            let id = this.props.match.params.id;
            let url = ("https://siglo-xxi-products.azurewebsites.net/Products/v1/menu/"+id);
            axios.get(url)
            .then(response =>{
                this.setState({
                    recipe : response.data.recipe
                })
                
            })
    }

    render(){
        
        return(
               
            <React.Fragment>
       
                <HeaderRecetas></HeaderRecetas>
                <div className="container">
            <br></br>

                <table id="customers" className="table table-striped">
                    <thead>
                        <tr>
                        <th scope="col"></th>
                        <th scope="col"></th>
                        <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody> 
                        {this.state.recipe.map((value, index) => {
                           return (
                            <tr key={index} >
                            <td>{value.product.name}</td>
                            <td>{value.quantity.value}</td>

                            </tr>
                            )
                        })}
                         
                 
                    </tbody>
                    </table>
                    <div>
                        <a className="btn btn-success" href="/IngresarMesa" role="button" style={{marginRight:"10px", float:"right"}}>
                        Ingresar Mesa
                            </a>   
                     </div>
                </div>

                    
            </React.Fragment>
          
        );
    }

}


export default VerReceta;
