import  { Component }  from "react";
import React from "react";
import {ApiurlUsers} from '../services/apirest';
import axios from 'axios';
import Header from "../template/Header";
  

class Editar extends Component{


    state = {
        form: {
            "name": ""	,
            "birthdate":	"",
            "email"	: "",
            "code":	"",
            "namerole": ""	
            
        },
        error:false,
        errorMsg:""
    }


    componentDidMount(){
      let UserId = this.props.match.params.id;
      let url = ApiurlUsers + UserId;
      axios.get(url)
      .then ( response =>{
        this.setState({
          form:{
              name: response.data.user.name,
              birthdate:	response.data.user.birthdate,
              email	: UserId,
              code:	response.data.user.role.code,
              namerole: 	response.data.user.role.name
           
              
              
          }
        })
      });

    }


    manejadorChange = async e => {
      await this.setState({
          form:{
              ...this.state.form,
              [e.target.name]: e.target.value
          }
      })
   
}








render(){

    const form = this.state.form;
 

    const handleClick = async () => {
      const response = await axios
          .patch("https://siglo-xxi-users.azurewebsites.net/Users/v1/users/cliente@gmail.com", this.state.form)
          .catch((error) => console.log('Error: ', error));
      if (response && response.data) {
          console.log(response);
          console.log(response.data);
      }
  };


    
    return(

      <React.Fragment>

        <Header/>
          
        <div className="container">
                    <h3>Editar Usuario </h3>
            </div>

            <div className="container">
              <br/>
              <form className="form horizontal">
                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Nombre</label>
                    <div className="col-md-8">
                        <input className="form-control" name="name" placeholder="Nombre" type="text" 
                        value={form.name}
                        onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                
                <br/>
                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Fecha de nacimiento</label>
                    <div className="col-md-8">
                        <input  className="form-control" name="birthdate" placeholder="Fecha de nacimiento" type="text"
                    value={form.birthdate}
                    onChange={this.manejadorChange}

                        />
                      </div>
               </div>
                </div>

                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Email</label>
                    <div className="col-md-8">
                        <input className="form-control" name="email" placeholder="Email" type="text" 
                     value={form.email}
                     onChange={this.manejadorChange}

                        />
                      </div>
               </div>
                </div>
                
                <br/>
                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">code</label>
                    <div className="col-md-8">
                        <input className="form-control" name="code" placeholder="codigo rol" type="text" 
                     value={form.code}
                     onChange={this.manejadorChange}

                        />
                      </div>
               </div>
                </div>
                
                <br/>
             
                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">rol</label>
                    <div className="col-md-8">
                        <input className="form-control" name="name" placeholder="nombre rol" type="text" 
                     value={form.namerole}
                     onChange={this.manejadorChange}

                        />
                      </div>
               </div>
                </div>
                
                <br/>
              <br/>
                <button type="button "onClick={handleClick} className="btn btn-primary" style={{marginRight:"10px"}} >Editar</button>
                <button  type="submit" className="btn btn-danger" style={{marginRight:"10px"}}>Eliminar</button>
                <a className="btn btn-dark" href="/dashboard" >Salir</a>

              </form>
            </div>




      </React.Fragment>


    );

    }
  }





export default Editar;
