import React, {Component} from "react";
import {Apiproduct} from '../services/apirest';
import axios from 'axios';
import { createProduct } from "../converters/productConverter";
import swal from 'sweetalert';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faTrash} from '@fortawesome/free-solid-svg-icons';
import {faEdit} from '@fortawesome/free-solid-svg-icons';
import { faArrowAltCircleLeft} from '@fortawesome/free-solid-svg-icons'


class EditarProducto extends Component{

    state = {
        form:{
                "id": "",
                "name": "",
                "stock": "",
                                    
        },
        error:false,
        errorMsg:""
    }


    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };
    
  
      delete = () => {
        let productId = this.props.match.params.id;
        let url = Apiproduct + productId;
        let datos = {
          productId: productId,
        };
        swal({
          title: "Deseas eliminar el producto?",
          icon: "warning",
          buttons: true,
          dangerMode: true,
      })
          .then((willDelete) => {
              if (willDelete) {
        axios.delete(url, { headers: datos }).then((response) => {
          swal(
            'Producto eliminado',
            'El producto ha sido eliminado exitosamente.',
            'success' 
        )
          this.props.history.push("/productos");
        });
      } else {
        swal(
            'Tranquilo',
            'No se ha borrado el producto',
            'success'
        )
    }
    });
      };
  


      patch = () => {
        let idProduct = this.props.match.params.id;
        let url = Apiproduct + idProduct;
        console.log(this.state);
        const body = createProduct(this.state.form);
        axios
          .patch(url, body)
          .then((response) => {
            swal(
              'Producto modificado',
              'El producto ha sido actualizado exitosamente.',
              'success' 
          )
            console.log(response.data);
            this.props.history.push("/productos");
          })
          .catch((error) => console.log(error.response.data));
      };
    



  componentDidMount(){

    let idProduct = this.props.match.params.id;  
    let url = Apiproduct + idProduct; 
    axios.get(url)
    .then( response => {


       this.setState({
        
            form:{
                id: response.data.product.id,
                name:  response.data.product.name,
                stock:  response.data.product.stock    

            }
            
       });
       
    });
     
}




numeros(e) {
  const re = /[0123456789]+/g;
  if (!re.test(e.key)) {
    e.preventDefault();
  }
}


fourthMethod(e) {
  const re = /[a-zA-Z" "ñÑ]+/g;
  if (!re.test(e.key)) {
    e.preventDefault();
  }
}




    render(){

        
        const form = this.state.form;
    
        return(
               
         <React.Fragment>

         <br/>
         
         <div className="container">
         <a id="volver" className="btn btn-dark" href="/productos" > <FontAwesomeIcon  icon={faArrowAltCircleLeft}/> Volver</a>
<hr></hr>
                    <h3><strong>Editar Producto</strong> </h3>
         </div>
         <br/>
         <div className="container">
         <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
       
      

                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Nombre</label>
                    <div className="col-md-6">
                        <input className="form-control" name="name" placeholder="Nombre del producto" type="text" 
                        value={form.name  || ''}
                        onChange={this.manejadorChange}
                        maxLength={30}
                        onKeyPress={(e) => this.fourthMethod(e)}

                        />
                      </div>
               </div>
                </div>
                <br/>

                
         <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Cantidad</label>
                    <div className="col-md-4">
                        <input className="form-control" name="stock" placeholder="Cantidad" type="text" 
                        value={form.stock  || ''}
                        onChange={this.manejadorChange}
                        maxLength={3}
                        onKeyPress={(e) => this.numeros(e)}
                        />
                      </div>
               </div>
                </div>
                <br/>



          <br/>
<div className="container">
              <button 
              type="submit"
               className="btn btn-primary"
                style={{ marginRight: "10px" }}
                onClick={() => this.patch()}
                >
                   <FontAwesomeIcon icon={faEdit}/>
                     &nbsp;Editar
                </button>

              <button 
                type="submit"
                className="btn btn-danger"
                style={{ marginRight: "10px" }} 
                onClick={() => this.delete()}
                href="/productos"
                >
                 <FontAwesomeIcon icon={faTrash}/>
                   &nbsp; Eliminar
                  </button>
              
          </div>
          </form>
          </div>
        </React.Fragment>
        
               
        );
    }

}


export default EditarProducto;
