import React, {Component} from "react";
import {ApiMenus,ApiCategory} from '../services/apirest';
import axios from 'axios';
import { createMenu } from "../converters/menuConverter";
import swal from 'sweetalert';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faTrash} from '@fortawesome/free-solid-svg-icons';
import {faEdit} from '@fortawesome/free-solid-svg-icons';
import { faArrowAltCircleLeft} from '@fortawesome/free-solid-svg-icons'
import Main2 from  './Main2'




class VerReceta extends Component{

    state={
        recipe:[]
    } ;


    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };
    
  


  componentDidMount(){


    let idMenu = this.props.match.params.id;  
    let url = ApiMenus + idMenu; 
    axios.get(url)
    .then( response => {
       this.setState({
        recipe : response.data.recipe,
            
       });
      
       
    });
     

    
}






    render(){

        const form = this.state.form;

        return(
               
         <React.Fragment>
<Main2></Main2>
         <br/>

         <div className="container">
         <a id="volver" className="btn btn-dark" href="/menus" > <FontAwesomeIcon  icon={faArrowAltCircleLeft}/> Volver</a>
<hr></hr>
                    <h3><strong>Receta</strong></h3>
         </div>
         <br/>
         <div className="container">
         <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
       
  



            <br />      
            




                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Nombre&nbsp;del&nbsp;menú</label>
                    <div className="col-md-6">
                        <input className="form-control" name="name" placeholder="Nombre del menú" type="text" 
                      value={form.name || ''}
                      onChange={this.manejadorChange}
                      maxLength={30}
                        />
                      </div>
               </div>
                </div>
                <br/>


              <button 
              type="submit" 
              className="btn btn-danger"
               style={{ marginRight: "10px" }} 
               onClick={() => this.delete()}
                >
                  <FontAwesomeIcon icon={faTrash}/>
                   &nbsp; Eliminar
                  </button>
              

          
          </form>
          </div>
        </React.Fragment>
        
               
        );
    }

}


export default VerReceta;
