import React, {Component} from "react";
//css
import "../assets/css/Login.css";
import logo from '../assets/images/LOGOPRO.png';
import {Apiurl} from '../services/apirest';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import  {Link} from 'react-router-dom';

class Register extends Component{


    constructor(props){
        super(props);
    }


    state={
        form:{
            "username": "", 
            "password": ""
        },
        error:false,
        errorMsg:""
    }




    manejadorSubmit = e => {
        e.preventDefault();
    }


    manejadorChange = async e => {
            await this.setState({
                form:{
                    ...this.state.form,
                    [e.target.name]: e.target.value
                }
            })
         
    }


    manejadorBoton=()=>{
        //roles setearlos en los estados un estado rol quizas ir validando si ese rol puede entrar a esa pagina
        let url = Apiurl;
        axios.post(url,this.state.form)
        .then( response => {{
                localStorage.setItem("token", response.data.token); 
                localStorage.setItem("email", this.state.form.username); 
                localStorage.setItem("name", response.data.user.name); 
                
                this.props.history.push("/main");

            }
        }).catch( error => {
            console.log(error);
            this.setState({
                error : true,
                errorMsg : error.response.data.message
            });
            

        })
    }



    
    render(){



        
        return(
               
        <React.Fragment>

                    <div id="fondito" className="wrapper fadeInDown">
                    <div id="formContent">
                    
                        <div className="fadeIn first">
                        <img src={logo} width="120px" alt="logotipo"    style={{ marginTop: "10px" }} />
                        </div>

                      

                    </div>
                    </div>

                   
        </React.Fragment>
        
               
        );
    }

}


export default Register;
