import React, {Component} from "react";
import axios from "axios";
import { createUser } from "../converters/userConverter";
import { ApiurlUsers1 } from "../services/apirest";
import { ApiurlRoles } from "../services/apirest";
import swal from 'sweetalert';



class IngresarUsuario extends Component{



  state = {
    form:{
          
            "number": "",
            "validator": "",
            "name": "",
            "last_name": "",
            "second_last_name": "",
            "birthdate": "",
            "email": "",
            "password": "",
            "code": "",
            
                                
    },
    error:false,
    errorMsg:"",
    roles:[]
   
}




    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };

componentDidMount(){
  axios
      .get("https://siglo-xxi-users.azurewebsites.net/Users/v1/roles")
      .then((response) => this.setState({ roles: response.data.roles }));
}

      post = () => {
        let url = ApiurlUsers1;
        console.log(this.state);
        const body = createUser(this.state.form);
        axios
          .post(url, body)
          .then((response) => {
            
          swal(
            'Usuario Creado',
            'El Usuario fue creado exitosamente.',
            'success' 
        )
        
            console.log(response.data);
            this.props.history.push("/dashboard");
          })
          .catch((error) => console.log(error.response.data));
      };
    
  

      


    render(){   
      const form = this.state.form;

      function checkRut(rut) {
        // Despejar Puntos
        var valor = rut.value.replace('.','');
        // Despejar Guión
        valor = valor.replace('-','');
        
        // Aislar Cuerpo y Dígito Verificador
        cuerpo = valor.slice(0,-1);
        dv = valor.slice(-1).toUpperCase();
        
        // Formatear RUN
        rut.value = cuerpo + '-'+ dv
        
        // Si no cumple con el mínimo ej. (n.nnn.nnn)
        if(cuerpo.length < 7) { rut.setCustomValidity("RUT Incompleto"); return false;}
        
        // Calcular Dígito Verificador
        suma = 0;
        multiplo = 2;
        
        // Para cada dígito del Cuerpo
        for(i=1;i<=cuerpo.length;i++) {
        
            // Obtener su Producto con el Múltiplo Correspondiente
            index = multiplo * valor.charAt(cuerpo.length - i);
            
            // Sumar al Contador General
            suma = suma + index;
            
            // Consolidar Múltiplo dentro del rango [2,7]
            if(multiplo < 7) { multiplo = multiplo + 1; } else { multiplo = 2; }
      
        }
        
        // Calcular Dígito Verificador en base al Módulo 11
        dvEsperado = 11 - (suma % 11);
        
        // Casos Especiales (0 y K)
        dv = (dv == 'K')?10:dv;
        dv = (dv == 0)?11:dv;
        
        // Validar que el Cuerpo coincide con su Dígito Verificador
        if(dvEsperado != dv) { rut.setCustomValidity("RUT Inválido"); return false; }
        
        // Si todo sale bien, eliminar errores (decretar que es válido)
        rut.setCustomValidity('');
    }
        return(
               
          <React.Fragment>

          <br/>
          
          <div className="container">
                     <h3>Ingresar Usuario </h3>
          </div>
          <br/>
          <div className="container">
          <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
        
         
 
                 <div className="row">
                      <div className="col-sm-8">
                     <label className="col-md-2 control-label">Rut</label>
                     <div className="col-md-8">
                         <input className="form-control" name="number" placeholder="rut" type="text" 
                         onChange={this.manejadorChange}
                         />
                       </div>
                </div>
                 </div>
                 <br/>
                 <br/>
                 

                 
                 <div className="row">
                      <div className="col-sm-8">
                     <label className="col-md-2 control-label">Digito&nbsp;verificador</label>
                     <div className="col-md-8">
                         <input className="form-control" name="validator" placeholder="Digito verificador" type="text" 
                         onChange={this.manejadorChange}
                         />
                       </div>
                </div>
                 </div>
                 <br/>
                 <br/>
            
                 <div className="row">
                      <div className="col-sm-8">
                     <label className="col-md-2 control-label">Nombre</label>
                     <div className="col-md-8">
                         <input className="form-control" name="name" placeholder="Nombre" type="text" 
