import React, {Component} from "react";
import Header from "../template/Header";
import { Apiproduct } from "../services/apirest";
import axios from 'axios';


class Dashboard extends Component{

    state={
        products:[]
    } ;


    clickProducts(id){
        this.props.history.push("/editarproducto/"+ id);
    }


    
    componentDidMount(){
            let url = Apiproduct;
            axios.get(url)
            .then(response =>{
                this.setState({
                    products : response.data.products
                })
                
            })
    }

    render(){
        
        return(
               
            <React.Fragment>

                <Header></Header>
                <div className="container">
            <br></br>

                <table className="table table-striped">
                    <thead>
                        <tr>
                        <th scope="col">id</th>
                        <th scope="col">name</th>
                        <th scope="col">stock</th>
             
                        </tr>
                    </thead>
                    <tbody> 
                        {this.state.products.map((value, index) => {
                           return (
                            <tr key={index} onClick={ () => this.clickProducts(value.id)  }  >
                            <td>{value.id}</td>
                            <td>{value.name}</td>
                            <td>{value.stock}</td>
                            </tr>
                            )
                        })}
                         
                 
                    </tbody>
                    </table>
                    <div>
                        <a className="btn btn-success" href="/productos" role="button" style={{marginLeft:"10px"}}>Ingresar Producto</a>   
                     </div>
                </div>

                    
            </React.Fragment>
          
        );
    }

}


export default Dashboard;
