import React, {Component} from "react";
import axios from "axios";
import { createMesa } from "../converters/mesaConverter";
import { ApiMesas1 } from "../services/apirest";
import swal from 'sweetalert';


class IngresarMesa extends Component{



  state = {
    form:{
          
            "number": "",
            "code":"",
            "name":""
            
                                
    },
    error:false,
    errorMsg:"",
    status:[]
}




    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };



      post = () => {
        let url = ApiMesas1;
        console.log(this.state);
        const body = createMesa(this.state.form);
        axios
          .post(url, body)
          .then((response) => {
            swal(
              'Mesa Creada',
              'La mesa ha sido creada exitosamente.',
              'success' 
          )
            console.log(response.data);
            this.props.history.push("/mesas");
          })
          .catch((error) => console.log(error.response.data));
      };
    
  

      componentDidMount(){

        axios
        .get("https://siglo-xxi-tables.azurewebsites.net/Tables/v1/tables/status")
        .then((response) => this.setState({ status: response.data.status }));
      
      }



    render(){   
      const form = this.state.form;
        return(
               
          <React.Fragment>

          <br/>
          
          <div className="container">
                     <h3>Ingresar Mesa </h3>
          </div>
          <br/>
          <div className="container">
          <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
        
         
 
                 <div className="row">
                      <div className="col-sm-8">
                     <label className="col-md-2 control-label">Numero de mesa</label>
                     <div className="col-md-8">
                         <input className="form-control" name="number" placeholder="Numero de mesa" type="text" 
                         value={form.number  || ''}
                         onChange={this.manejadorChange}
                         />
                       </div>
                </div>
                 </div>
                 <br/>
 



               {/*  <div className="row">
                      <div className="col-sm-8">
                     <label className="col-md-2 control-label">Estado mesa 1-4</label>
                     <div className="col-md-8">
                         <input className="form-control" name="code" placeholder="Estado mesa" type="text" 
                         value={form.code  || ''}
                         onChange={this.manejadorChange}
                         />
                       </div>
                </div>
                 </div>
        <br/>*/}
 



                 

  <div className="row">
              <div className="col-sm-4">
                <label className="col-md-2 control-label">Estado</label>
                <div className="col-md-6">
                  <select
                    className="form-select"
                    aria-label="default select example" 
                    name="code"
                    value={this.code}
                    onChange={this.manejadorChange}
                  >
                    {this.state.status.map((status) => {
                      return <option key={status.code} value={status.code}>{status.name}</option>
                    })}
                  </select>
                </div>
              </div>
            </div>

                  <br />






    
           <br/>
 
               <button 
               type="submit"
                className="btn btn-primary"
                 style={{ marginRight: "10px" }}
                 onClick={() => this.post()}
                 >
                   Agregar Mesa
                 </button>
 
             
               <a className="btn btn-dark" href="/mesas" >Salir</a>
               
           
           </form>
           </div>
         </React.Fragment>
        
               
        );
    }

}


export default IngresarMesa;
