import React, {Component} from "react";
import axios from "axios";
import { createMesa } from "../converters/mesaConverter";
import { ApiMesas1 } from "../services/apirest";
import swal from 'sweetalert';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowAltCircleLeft} from '@fortawesome/free-solid-svg-icons'

class IngresarReserva extends Component{


  state = {
    form:{
          
    
            "code":"",
            "name":""
            
                                
    },
    error:false,
    errorMsg:"",

}




    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };


    
  



      componentDidMount(){

  
        axios.get("https://siglo-xxi-reservations.azurewebsites.net/Reservations/v1/reservations/dates")
        .then( response => {
           this.setState({
            
                form:{
        
                    code:  response.data.code,
                    name:  response.data.name  
    
                },
                
           });
           
        });
   
    }
      
    
    render(){   
      const form = this.state.form;
        return(
               
          <React.Fragment>

          <br/>
          
          <div className="container">
          <a id="volver" className="btn btn-dark" href="/reservaciones" > <FontAwesomeIcon  icon={faArrowAltCircleLeft}/> Volver</a>
<hr></hr>
                     <h3><strong>Ingresar Reservación</strong> </h3>
          </div>
          <br/>
          <div className="container">
          <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
        
         
 
 






    
           <br/>
 
               <button 
               type="submit"
                className="btn btn-primary"
                 style={{ marginRight: "10px" }}
                 onClick={() => this.post()}
                 >
                   Ingresar reservación
                 </button>
 
             
               
           
           </form>
           </div>
         </React.Fragment>
        
               
        );
    }

}


export default IngresarReserva;
