import React, {Component} from "react";
//css
import "../assets/css/Login.css";
import logo from '../assets/images/LOGOPRO.png';
import {Apiurl} from '../services/apirest';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import {reactLocalStorage} from 'reactjs-localstorage';
 
reactLocalStorage.set('var', true);
reactLocalStorage.get('var', true);
reactLocalStorage.setObject('var', {'test': 'test'});
reactLocalStorage.getObject('var');
reactLocalStorage.remove('var');
reactLocalStorage.clear();

class Login extends Component{




    state={
        form:{
            "username": "", 
            "password": ""
        },
        error:false,
        errorMsg:""
    }




    manejadorSubmit = e => {
        e.preventDefault();
    }


    manejadorChange = async e => {
            await this.setState({
                form:{
                    ...this.state.form,
                    [e.target.name]: e.target.value
                }
            })
         
    }


    manejadorBoton=()=>{
        
        let url = Apiurl;
        axios.post(url,this.state.form)
        .then( response => {
            if(response.data.status === "ok"){
                localStorage.setItem("token", response.data.data.token); 
                

            }else{
                this.setState({
                    error : true,
                    errorMsg: error.response.data.message
                    
                })
            console.log(response.data.token);

            }
        }).catch( error => {
            console.log(error);
            this.setState({
                error : true,
                errorMsg : "error api"
            });

        })
    }


    render(){
        return(
               
        <React.Fragment>

                    <div className="wrapper fadeInDown">
                    <div id="formContent">
                    
                        <div className="fadeIn first">
                        
                        <div id="logo">
                        <img src={logo} width="120px"  className="corazon" alt="logotipo" />
                        
                        </div>
                        </div>

                        <form onSubmit={this.manejadorSubmit}>
                        <input type="text"  className="fadeIn second" name="username" placeholder="username"  onChange={this.manejadorChange}/>
                        <input type="password"  className="fadeIn third" name="password" placeholder="Password"  onChange={this.manejadorChange}/>
                        <input type="submit" className="fadeIn fourth" value="Log In" onClick={this.manejadorBoton}/>
                        </form>

                    {this.state.error === true &&
                        <div className="alert alert-danger" role="alert">
                            {this.state.errorMsg}
                        </div>

                    }

                    </div>
                    </div>


        </React.Fragment>
        
               
        );
    }

}


export default Login;
