import React, {Component} from "react";
import {ApiurlRoles} from '../services/apirest';
import Header from '../template/Header';
import axios from 'axios';

class Roles extends Component{

    state = {
      form :{
        "code": "",
        
      },
      error: false,
      errorMsg: ""
    }

    manejadorChange = async e => {
      await this.setState({
          form:{
              ...this.state.form,
              [e.target.name]: e.target.value
          }
      })
 }




    componentDidMount(){
        let url = ApiurlRoles;
      axios.get(url)
      .then( response => {
        this.setState({
          form : {
            code: response.data.roles
            
          }
        })
        console.log(response.data.roles);
       
      });

    }


    render(){


        return(
               
         <React.Fragment>

        
         
         
         <form className="form-horizontal ">
         <div className="row">
                     <div className="col-sm-4">
                    <label className="col-md-2 control-label">Role</label>
                    <div className="col-md-6">
                    <select className="roles" id="roles" onChange={this.manejadorChange}  >
                    <option id="1" value="ADMIN">ADMIN</option>
                    <option id="2" value="CLIENTE">CLIENTE</option>
                    <option id="3" value="BODEGA">BODEGA</option>
                    <option id="4" value="FINANZA">FINANZA</option>
                    <option id="5" value="COCINA">COCINA</option>

                   
 
                </select>
                      </div>
               </div>
                </div>






          
          </form>
        </React.Fragment>
        
               
        );
    }

}


export default Roles;
