import React, {Component} from "react";
import {ApiurlRoles} from '../services/apirest';
import {ApiurlUsers} from '../services/apirest';
import Header from '../template/Header';
import axios from 'axios';

class Editar extends Component{

    state = {
      form :{
        "rol": "",
        
      },
      error: false,
      errorMsg: ""
    }

    manejadorChange = async e => {
      await this.setState({
          form:{
              ...this.state.form,
              [e.target.name]: e.target.value
          }
      })
 }




    componentDidMount(){
        let url = ApiurlRoles;
        let url1 = ApiurlUsers;
        let idUsuario = this.props.match.params.id;
        console.log(url1);
      axios.get(url, url1)
      .then( response => {
        this.setState({
          form : {
            rol: response.data.roles
            
          }
        })
        console.log(response.data.roles);
       
      });

    }


    render(){


        return(
               
         <React.Fragment>

         <Header/> 
         <br/>
         
         <div className="container">
                    <h3>Editar Usuario </h3>
         </div>
         <form className="form-horizontal ">
         <div className="row">
                     <div className="col-sm-4">
                    <label className="col-md-2 control-label">Rol</label>
                    <div className="col-md-6">
                    <select className="roles" id="roles" onChange={this.manejadorChange}  >
                    <option id="1" value="ADMIN">ADMIN</option>
                    <option id="2" value="CLIENTE">CLIENTE</option>
                    <option id="3" value="BODEGA">BODEGA</option>
                    <option id="4" value="FINANZA">FINANZA</option>
                    <option id="5" value="COCINA">COCINA</option>

                   
 
                </select>
                      </div>
               </div>
                </div>




          <br/>

              <button type="submit" className="btn btn-primary" style={{ marginRight: "10px" }}>Editar</button>
              <button type="submit" className="btn btn-danger" style={{ marginRight: "10px" }}>Eliminar</button>
              <a className="btn btn-dark" href="/dashboard" >Salir</a>
              

          
          </form>
        </React.Fragment>
        
               
        );
    }

}


export default Editar;
