  const re = /[a-zA-Z" "ñÑ0123456789.,¡!]+/g;
  if (!re.test(e.key)) {
    e.preventDefault();
  }
}



numeros(e) {
  const re = /[0123456789]+/g;
  if (!re.test(e.key)) {
    e.preventDefault();
  }
}



    render(){

        const form = this.state.form;

        return(
               
         <React.Fragment>
<Main2></Main2>
         <br/>

         <div className="container">
         <a id="volver" className="btn btn-dark" href="/menus" > <FontAwesomeIcon  icon={faArrowAltCircleLeft}/> Volver</a>
<hr></hr>
                    <h3><strong>Receta</strong></h3>
         </div>
         <br/>
         <div className="container">
         <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
       
  


                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Precio&nbsp;del&nbsp;menú</label>
                    <div className="col-md-6">
                        <input className="form-control" name="amount" placeholder="Precio del menú" type="text" 
                     value={form.amount || ''}
                     onChange={this.manejadorChange}
           
                        />
                      </div>
               </div>
                </div>
                <br/>




          <br/>

              <button
               type="submit"
                className="btn btn-primary" 
                style={{ marginRight: "10px" }}
                onClick={() => this.patch()}
                >
                    <FontAwesomeIcon icon={faEdit}/>
                     &nbsp;Editar
                  </button>

              <button 
              type="submit" 
              className="btn btn-danger"
               style={{ marginRight: "10px" }} 
               onClick={() => this.delete()}
                >
                  <FontAwesomeIcon icon={faTrash}/>
                   &nbsp; Eliminar
                  </button>
              

          
          </form>
          </div>
        </React.Fragment>
        
               
        );
    }

}


export default VerReceta;
