import React, {Component} from "react";
import {ApiMenus,ApiCategory} from '../services/apirest';
import axios from 'axios';
import { createMenu } from "../converters/menuConverter";
import swal from 'sweetalert';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faTrash} from '@fortawesome/free-solid-svg-icons';
import {faEdit} from '@fortawesome/free-solid-svg-icons';
import { faArrowAltCircleLeft} from '@fortawesome/free-solid-svg-icons'
import Main2 from  './Main2'




class VerReceta extends Component{

    state = {
        form:{
        
            "name": "",
            "value": "",
            "measure": ""
        
          
        },
        error:false,
        errorMsg:"",


    }


    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };
    


  componentDidMount(){


    let idMenu = this.props.match.params.id;  
    let url = ApiMenus + idMenu; 
    axios.get(url)
    .then( response => {
       this.setState({
            form:{
               
                name: response.data.menu.recipe[0].product.name,
                value: response.data.menu.recipe[0].quantity.value,
                measure: response.data.menu.recipe[0].quantity.measure.name,



              },
            
       });
       console.log(response.data.menu.recipe)
       
    });
     

}




    render(){

        const form = this.state.form;

        return(
               
         <React.Fragment>
<Main2></Main2>
         <br/>

         <div className="container">
         <a id="volver" className="btn btn-dark" href="/menus" > <FontAwesomeIcon  icon={faArrowAltCircleLeft}/> Volver</a>
<hr></hr>
                    <h3><strong>Receta</strong></h3>
         </div>
         <br/>
         <div className="container">
         <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
       
  


                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Nombre&nbsp;del&nbsp;producto</label>
                    <div className="col-md-4">
                        <input className="form-control" name="name" placeholder="Nombre del producto" type="text" readOnly
                     value={form.name || ''}
                     onChange={this.manejadorChange}
           
                        />
                      </div>
               </div>
               <label className="col-md-2 control-label">Cantidad</label>
                    <div className="col-md-4">
                        <input className="form-control" name="value" placeholder="cantidad" type="text" readOnly
                     value={form.value || ''}
                     onChange={this.manejadorChange}
           
                        />
                      </div>
                </div>
                <br/>



                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Medida</label>
                    <div className="col-md-4">
                        <input className="form-control" name="measure" placeholder="cantidad" type="text" readOnly
                     value={form.measure || ''}
                     onChange={this.manejadorChange}
           
                        />
                      </div>
               </div>
                </div>
                <br/>



          <br/>

           
          </form>
          </div>
        </React.Fragment>
        
               
        );
    }

}


export default VerReceta;
