import React, {Component} from "react";
import HeaderMenu from "../template/HeaderMenu";
import { ApiMenus } from "../services/apirest";
import axios from 'axios';
import ImageDefault from '../assets/images/defaultimage.png';
import "../assets/css/Login.css";
import Main2 from './Main2';

class VerReceta extends Component{

  state={
    recipe:[]
  } ;


  clickMenus(id){
      this.props.history.push("/editarmenu/"+ id);
  }

  clickRecetas(id){
      this.props.history.push("/verreceta/"+ id);
      console.log(id);
  }

  
  componentDidMount(){
    let idMenu = this.props.match.params.id;  
    let url = ApiMenus + idMenu
       
          axios.get(url)
          .then(response =>{
              this.setState({
                recipe : response.data.recipe[0]
              })
              
          })
  }

  render(){
      
      return(
             
          <React.Fragment>
              <Main2></Main2>
              <HeaderMenu></HeaderMenu>
              <div className="container">
          <br></br>

              <table id="customers">
              <thead >
                      <tr>
 
                      <th scope="col">Descripci√≥n</th>
          
                      </tr>
                  </thead>
                  <tbody> 
                      {this.state.recipe.map((value, index) => {
                         return (
                          <tr key={index}   >
                          
                   
                          <td>{value.recipe.id}</td>
                         

                          </tr>
                          )
                      })}
                       
               
                  </tbody>
                  </table>
                  <br/>
                  <div>
                    
                   </div>
              </div>

                  
          </React.Fragment>
        
      );
  }

}


export default VerReceta;
