import React, {Component} from "react";
import {ApiMenus} from '../services/apirest';
import axios from 'axios';
import { createMenu } from "../converters/menuConverter";
import swal from 'sweetalert';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {faTrash} from '@fortawesome/free-solid-svg-icons';
import {faEdit} from '@fortawesome/free-solid-svg-icons';

class EditarMenu extends Component{

    state = {
        form:{
        
            "code": "",
            "name": "",
            "description": "",
            "amount": "",
            "image": "",
            "is_available": "",
                                   
        },
        error:false,
        errorMsg:""
    }


    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };
    
  
      
  patch = () => {
    let idMenu = this.props.match.params.id;
    let url = ApiMenus + idMenu;
    console.log(this.state);
    const body = createMenu(this.state.form);
    axios
      .patch(url, body)
      .then((response) => {
        swal(
          'Menú Modificado',
          'El menú ha sido actualizado exitosamente.',
          'success' 
      )
        console.log(response.data);
        this.props.history.push("/menus");
      })
      .catch((error) => console.log(error.response.data));
  };









      delete = () => {
        let menuId = this.props.match.params.id;
        let url = ApiMenus + menuId;
        let datos = {
          menuId: menuId,
        };
        swal({
          title: "Estas seguro?",
          text: "Borrarás permanentemente el menú",
          icon: "warning",
          buttons: true,
          dangerMode: true,
      })
          .then((willDelete) => {
              if (willDelete) {
        axios.delete(url, { headers: datos }).then((response) => {
          swal(
            'Menu eliminado',
            'El Menu ha sido eliminado exitosamente.',
            'success' 
        )
          this.props.history.push("/menus");
        });
      } else {
        swal(
            'Tranquilo',
            'No se ha borrado el menú',
            'success'
        )
    }
    });
      };




  componentDidMount(){


    let idMenu = this.props.match.params.id;  
    let url = ApiMenus + idMenu; 
    axios.get(url)
    .then( response => {
       this.setState({
            form:{
               
                code:  response.data.menu.category.code,
                name: response.data.menu.name,
          
                description:  response.data.menu.description,
                amount:  response.data.menu.price.amount,
                image: response.data.menu?.image?? "",
                is_available:  response.data.menu?.is_available?? "",

            },
            
       });
       
    });
     

    
}


    render(){

        const form = this.state.form;

        return(
               
         <React.Fragment>

         <br/>

         <div className="container">
                    <h3>Editar Menu </h3>
         </div>
         <br/>
         <div className="container">
         <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
       
  


                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Id Categoria</label>
                    <div className="col-md-8">
                        <input className="form-control" name="code" placeholder="codigo categoria" type="text" readOnly
                        value={form.code || ''}
                        onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                <br/>





                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Nombre plato</label>
                    <div className="col-md-8">
                        <input className="form-control" name="name" placeholder="Nombre plato" type="text" 
                      value={form.name || ''}
                      onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                <br/>


                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Descripcion</label>
                    <div className="col-md-8">
                        <input className="form-control" name="description" placeholder="descripcion plato" type="text" 
                      value={form.description || ''}
                      onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                <br/>

                
                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">PRECIO</label>
                    <div className="col-md-8">
                        <input className="form-control" name="amount" placeholder="precio plato" type="text" 
                     value={form.amount || ''}
                     onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                <br/>


                
                <div className="row">
                     <div className="col-sm-8">
                    <label className="col-md-2 control-label">Imagen</label>
                    <div className="col-md-8">
                        <input className="form-control" name="image" placeholder="imagen plato" type="text" 
                      value={form.image || ''}
                      onChange={this.manejadorChange}
                        />
                      </div>
               </div>
                </div>
                <br/>




        







          <br/>

              <button
               type="submit"
                className="btn btn-primary" 
                style={{ marginRight: "10px" }}
                onClick={() => this.patch()}
                >
                    <FontAwesomeIcon icon={faEdit}/>
                     &nbsp;Editar
                  </button>

              <button 
              type="submit" 
              className="btn btn-danger"
               style={{ marginRight: "10px" }} 
               onClick={() => this.delete()}
                >
                  <FontAwesomeIcon icon={faTrash}/>
                   &nbsp; Eliminar
                  </button>


              <a className="btn btn-dark" href="/menus" >Salir</a>
              

          
          </form>
          </div>
        </React.Fragment>
        
               
        );
    }

}


export default EditarMenu;
