import React, {Component} from "react";
import axios from "axios";
import { createMesa } from "../converters/mesaConverter";
import { ApiMesas1 } from "../services/apirest";
import swal from 'sweetalert';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowAltCircleLeft} from '@fortawesome/free-solid-svg-icons'

class IngresarReserva extends Component{


  state = {
    form:{
          
            "number": "",
            "code":"",
            "name":""
            
                                
    },
    error:false,
    errorMsg:"",
   
}




    manejadorChange = async (e) => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value,
          },
        });
      };


      manejadorSubmit = (e) => {
        e.preventDefault();
      };



      post = () => {
        let url = ApiMesas1;
        console.log(this.state);
        const body = createMesa(this.state.form);
        axios
          .post(url, body)
          .then((response) => {
            swal(
              'Mesa creada',
              'Mesa registrada con éxito.',
              'success' 
          )
            console.log(response.data);
            this.props.history.push("/mesas");
          })
          .catch((error) => console.log(error.response.data));
      };
    
  



      
      componentDidMount(){
        axios
        .get("https://siglo-xxi-reservations.azurewebsites.net/Reservations/v1/reservations/dates")
        .then((response) =>
          this.setState({
            form: { code: response.data[0].code },
            
          })); 
     
      }

      
    
    render(){   
      const form = this.state.form;
        return(
               
          <React.Fragment>

          <br/>
          
          <div className="container">
          <a id="volver" className="btn btn-dark" href="/reservaciones" > <FontAwesomeIcon  icon={faArrowAltCircleLeft}/> Volver</a>
<hr></hr>
                     <h3><strong>Ingresar Reservación</strong> </h3>
          </div>
          <br/>
          <div className="container">
          <form className="form-horizontal" onSubmit={this.manejadorSubmit}>
        
         
                
          <div className='row'>
              <div className='col-sm-4'>
                <label className='col-md-2 control-label'>Rol</label>
                <div className='col-md-6'>
                  <select
                    className='form-select'
                    name='code'
                    value={this.state.form.code}
                    onChange={this.manejadorChange}>
                    {this.state.dates.map((dates) => {
                      return (
                        <option key={dates.code} value={dates.code}>
                          {dates.name}
                        </option>
                      )
                    })}
                  </select>
                </div>
              </div>
            </div>
 
 






    
           <br/>
 
               <button 
               type="submit"
                className="btn btn-primary"
                 style={{ marginRight: "10px" }}
                 onClick={() => this.post()}
                 >
                   Agregar Mesa
                 </button>
 
             
               
           
           </form>
           </div>
         </React.Fragment>
        
               
        );
    }

}


export default IngresarReserva;
