import React, {Component} from 'react';
import Pelicula from './Pelicula';
class Peliculas extends Component{
    state = {
        peliculas: [
            {titulo: 'Batman vs Superman', image:'https://eldiariony.com/wp-content/uploads/sites/2/2016/03/batmanvsupermanweb.jpg?quality=60&strip=all&w=1200'},
            {titulo: 'Gran Torino', image: 'https://imagenes.lainformacion.com/files/image_656_370/uploads/imagenes/2016/09/11/clint-torino.jpg'},
            {titulo: 'Looper',image:'https://image.tmdb.org/t/p/w500/t6ogdhdjP9KDCLJR9i4HG3bplQL.jpg'}
        ],
        nombre: 'Matias Cisternas',
        favorita: {}

    };

    cambiarTitulo = () => {

        var { peliculas } = this.state;
       // var random = Math.floor(Math.random() * 3); 
        peliculas[0].titulo = "Batman Begins ";

        this.setState({
            peliculas: peliculas
        });
    }


    favorita = (pelicula) => {
         
        console.log("favorita marcada");
        console.log(pelicula);
        this.setState({
            favorita: pelicula
        });
         
    }




    
    render(){
        return(
            <div id= "content" className='peliculas'>

            <h2 className="subheader" >Peliculas</h2>
            <p>Seleccion de las peliculas favoritas de {this.state.nombre}</p>
            <p>
                <button onClick={this.cambiarTitulo}>
                Cambiar titulo de batman 
                </button>
            </p>
            <p>
                <strong>La pelicula favorita es: </strong>
                <span>{this.state.favorita.titulo}</span>
            </p>


            {/**crear componente pelicula */}
<div id="articles" className="peliculas"> 
            {
            this.state.peliculas.map((pelicula, i) => {
                return(
                    <Pelicula
                     key={i} 
                     pelicula={pelicula}
                     marcarFavorita={this.favorita}
                     />
                )
            })
          }
            </div>
            </div>
        );
    }


}

export default Peliculas;
