import React, {Component} from "react";
import Header from "../template/Header";
import { ApiMenus } from "../services/apirest";
import axios from 'axios';


class Menus extends Component{

    state={
        menu:[]
    } ;


    clickMenus(id){
        this.props.history.push("/editarmenu/"+ id);
    }


    
    componentDidMount(){
            let url = ApiMenus;
            axios.get(url)
            .then(response =>{
                this.setState({
                    menu : response.data.menu
                })
                
            })
    }

    render(){
        
        return(
               
            <React.Fragment>

                <Header></Header>
                <div className="container">
            <br></br>

                <table className="table table-striped">
                    <thead>
                        <tr>
                        <th scope="col">id</th>
                        <th scope="col">code</th>
                        <th scope="col">name</th>
                        <th scope="col">namefood</th>
                        <th scope="col">description</th>
                        <th scope="col">amount</th>
                        <th scope="col">image</th>
                        <th scope="col">is_available</th>
             
                        </tr>
                    </thead>
                    <tbody> 
                        {this.state.menu.map((value, index) => {
                           return (
                            <tr key={index} onClick={ () => this.clickMenus(value.id)  }  >
                            <td>{value.id}</td>
                            <td>{value.category.code}</td>
                            <td>{value.category.name}</td>
                            <td>{value.name}</td>
                            <td>{value.description}</td>
                            <td>{value.price.amount}</td>
                            <td>{value.image}</td>
                            <td>{value.is_available}</td>
                            </tr>
                            )
                        })}
                         
                 
                    </tbody>
                    </table>
                    <div>
                        <a className="btn btn-success" href="/  " role="button" style={{marginLeft:"10px"}}>Ingresar Menu</a>   
                     </div>
                </div>

                    
            </React.Fragment>
          
        );
    }

}


export default Menus;
